        private static string HtmlAgilityPackDailySentence(string content)
        {
            var dom = new HtmlDocument();
            dom.LoadHtml(content);

            return dom
                .DocumentNode.SelectSingleNode("//div[@class='vanfon_geyan']")
                .SelectSingleNode(".//span")
                .InnerText;
        }

        private static string RegexDailySentence(string content)
        {
            var match = DailySentenceRegex().Match(content);
            if (match.Success)
            {
                return match.Groups[1].Value;
            }
            return string.Empty;
        }

        [GeneratedRegex(
